    private void submitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitButtonActionPerformed
        // TODO add your handling code here:
        String nama = inNama.getText().toString().trim();
        String username = inUsername.getText().toString().trim();
        String password = inPassword.getText().toString().trim();
        
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement(); 
            PreparedStatement sql = c.prepareStatement("SELECT * FROM admin WHERE username = ?");
            sql.setString(1, username);
            ResultSet r = sql.executeQuery();
            if (r.next()) {
                JOptionPane.showMessageDialog(null, "Username telah dipakai!");
            }else{
                try{
                    try{
                        
                        String sqll = "INSERT INTO murid(Nama, Username, Password)"
                                + "VALUES('"+nama+"', '"+username+"', '"+password+"')";
                        PreparedStatement p = c.prepareStatement(sqll);
                        p.executeUpdate(sqll);
                        p.close();
                        JOptionPane.showMessageDialog(null, "Sukses menambahkan akun admin!");
                    }catch(SQLException e){
                        System.out.println("Error");
                    }
                    s.close();
                }catch(Exception e){
                    System.out.println("Check error");
                }
            }
            s.close();
            r.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_submitButtonActionPerformed

