    private void submitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitButtonActionPerformed
        // TODO add your handling code here:
        String nama = inNama.getText().toString().trim();
        String nim = inNoInduk.getText().toString().trim();
        String kelamin = (String) inKelamin.getSelectedItem();
        String darah = (String) inGolDarah.getSelectedItem();
        String hp = inNoHP.getText().toString().trim();
        String wali = inWali.getText().toString().trim();
        String hpwali = inHpWali.getText().toString().trim();
        
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement(); 
            try{
                String sql = "INSERT INTO murid(Nama, NoInduk, Kelamin, GolDarah, HP, Wali, HPWali)"
                        + "VALUES('"+nama+"', '"+nim+"', '"+kelamin+"', '"+darah+"', '"+hp+"', '"+wali+"', '"+hpwali+"')";
                PreparedStatement p = c.prepareStatement(sql);
                p.executeUpdate(sql);
                p.close();
                JOptionPane.showMessageDialog(null, "Sukses menambahkan data siswa!");
            }catch(SQLException e){
                System.out.println("Error");
            }
            s.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_submitButtonActionPerformed

