    private void jLabel7MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel7MouseClicked
        // TODO add your handling code here:

        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement();
            String user = inUser.getText().toString().trim();
            String sql = "SELECT * FROM admin WHERE Username = '"+user+"' ";
            ResultSet r = s.executeQuery(sql);
            if (r.next()) {
                inNama.setText(r.getString("Nama"));
                inUsername.setText(r.getString("Username"));
                inPassword.setText(r.getString("Password"));
            }else{
                System.out.println("Admin tidak ditemukan!");
            }
            s.close();
            r.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_jLabel7MouseClicked

    private void UpdateButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_UpdateButtonMouseClicked
        // TODO add your handling code here:
        String nama = inNama.getText().toString().trim();
        String username = inUsername.getText().toString().trim();
        String password = inPassword.getText().toString().trim();
        String user = inUser.getText().toString().trim();

        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement();
            try{
                String sql = "UPDATE admin SET Nama = '"+nama+"', Username = '"+username+"', Password = '"+password+"' WHERE Username = '"+user+"' ";
                PreparedStatement p = c.prepareStatement(sql);
                p.executeUpdate(sql);
                p.close();
                JOptionPane.showMessageDialog(null, "Sukses mengupdate data admin!");
            }catch(SQLException e){
                System.out.println("Error");
            }
            s.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_UpdateButtonMouseClicked

