    private void jLabel7MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel7MouseClicked
        // TODO add your handling code here:
        
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement(); 
            String nim = inNim.getText().toString().trim();
            String sql = "SELECT * FROM murid WHERE NoInduk = '"+nim+"' ";
            ResultSet r = s.executeQuery(sql);
            if (r.next()) {
                inNama.setText(r.getString("Nama"));
                inNoInduk.setText(r.getString("NoInduk"));
                inKelamin.setText(r.getString("Kelamin"));
                inDarah.setText(r.getString("GolDarah"));
                inHp.setText(r.getString("HP"));
                inWali.setText(r.getString("Wali"));
                inHpWali.setText(r.getString("HPWali"));
            }else{
                JOptionPane.showMessageDialog(null, "Murid tidak ditemukan!");
            }
            s.close();
            r.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_jLabel7MouseClicked

    private void UpdateButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_UpdateButtonMouseClicked
        // TODO add your handling code here:
        String nama = inNama.getText().toString().trim();
        String noinduk = inNoInduk.getText().toString().trim();
        String kelamin = inKelamin.getText().toString().trim();
        String darah = inDarah.getText().toString().trim();
        String hp = inHp.getText().toString().trim();
        String wali = inWali.getText().toString().trim();
        String hpwali = inHpWali.getText().toString().trim();
        String nim = inNim.getText().toString().trim();
        
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement(); 
            try{
                String sql = "UPDATE murid SET Nama = '"+nama+"', NoInduk = '"+noinduk+"', Kelamin = '"+kelamin+"', GolDarah  = '"+darah+"', HP = '"+hp+"', Wali = '"+wali+"', HPWali = '"+hpwali+"' WHERE NoInduk = '"+nim+"'";
                PreparedStatement p = c.prepareStatement(sql);
                p.executeUpdate(sql);
                p.close();
                JOptionPane.showMessageDialog(null, "Sukses mengupdate data siswa!");
            }catch(SQLException e){
                System.out.println("Error");
            }
            s.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_UpdateButtonMouseClicked

    private void DeleteButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_DeleteButtonMouseClicked
        // TODO add your handling code here:
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement(); 
            try{
                String nim = inNim.getText().toString().trim();
                String sql = "DELETE FROM murid WHERE NoInduk = '"+nim+"'";
                PreparedStatement p = c.prepareStatement(sql);
                p.executeUpdate(sql);
                p.close();
                JOptionPane.showMessageDialog(null, "Sukses menghapus data siswa!");
            }catch(SQLException e){
                System.out.println("Error");
            }
            s.close();
        }catch(Exception e){
            System.out.println("Check error");
        }
    }//GEN-LAST:event_DeleteButtonMouseClicked

