    private void submitButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitButton1ActionPerformed
        // TODO add your handling code here:
        try{
            Connection c = Koneksi.getKoneksi();
            Statement s = c.createStatement();

            String sql = "SELECT * FROM admin WHERE Username='" + inUsername1.getText()
            + "' and Password='" + inPassword1.getText() + "'";
            ResultSet r = s.executeQuery(sql);

            if (r.next()){
                JOptionPane.showMessageDialog(null, "Login Sukses!");
                this.dispose();
                ProfileOption a = new ProfileOption();
                a.setVisible(true);
            }else {
                JOptionPane.showMessageDialog(null, "Salah Password atau Username!");
                inPassword1.requestFocus();
            }
        }catch(SQLException e){
            System.out.println("error");
        }
    }//GEN-LAST:event_submitButton1ActionPerformed

